import React, { Component, Fragment } from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,Row,Col
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    <React.Fragment>
      
          <Col xs="3">
      <Card key={book.id}>
        <CardImg alt="cover" src={book.cover} top width="100%" />
        <CardBody>
          <CardTitle tag="h5">{book.title}</CardTitle>
          <CardSubtitle className="mb-2 text-muted" tag="h6">
            {book.author}
          </CardSubtitle>
          <Button outline>Read</Button>{" "}
          <Button color="danger" outline>
            Remove
          </Button>{" "}
          <IoHeartOutline size={35} />
        </CardBody>
      </Card>
      </Col>
        
    </React.Fragment>
  );
};
export default ItemLibrary;
