import React from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    <React.Fragment>
      <div style={{
          borderRadius: "15px",
          flex: "1 1 21rem",
          display: "flex",
          flexWrap: "wrap",
          flexDirection: "column",
          alignSelf: "normal",
          maxWidth: "250px",
        }}>
        <Card
        key={book.id}
        className="m-3 p-3"
        style={{
          borderRadius: "15px",
          flex: "1 1 21rem",
          display: "flex",
          flexWrap: "wrap",
          flexDirection: "column",
          alignSelf: "normal",
          maxWidth: "250px",
        }}
      >
        <CardImg
          alt="cover"
          src={book.formats["image/jpeg"]}
          top
          width="100%"
          style={{ maxHeight: "300px" }}
        />
        <CardBody>
          <CardTitle tag="h5">{book.title}</CardTitle>
          <CardSubtitle className="mb-2 text-muted" tag="h6">
            {book.authors[0].name}
          </CardSubtitle>
          <a href={book.url}>
            <Button outline style={{marginBottom:"-50px"}}>Read</Button>
          </a>{" "}
          {/* <Button color="danger" outline>
                  Remove
                </Button>{" "} */}
          <IoHeartOutline
            size={30}
            style={{ position: "absolute", bottom: "35", right: "35" }}
          />
        </CardBody>
      </Card>
      </div>
      
    </React.Fragment>
  );
};
export default ItemLibrary;
