import React, { Component } from "react";
import {
  Col,
  Container,
  Row,
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  CardText,
  Button,
} from "reactstrap";
import { BsBookmarkHeart } from "react-icons/bs";

export default class Library extends Component {
  renderSummary() {
    return (
      <div>
        <Container>
          <Row>
            <Col xs="3">
              {this.props.library.map((book) => (
                <Card>
                  <CardImg
                    alt="Card image cap"
                    src={book.cover}
                    top
                    width="100%"
                  />
                  <CardBody>
                    <CardTitle tag="h5">{book.title}</CardTitle>
                    <CardSubtitle className="mb-2 text-muted" tag="h6">
                      {book.author}
                    </CardSubtitle>
                    <CardText>{book.tags}</CardText>
                    <Button
                      outline
                      onClick={() => this.props.readBook(libraryItem.book)}
                    >
                      {" "}
                      Read{" "}
                    </Button>{" "}
                    <Button
                      color="danger"
                      outline
                      onClick={() =>
                        this.props.removeFromLibrary(libraryItem.book)
                      }
                    >
                      {" "}
                      Remove{" "}
                    </Button>{" "}
                    <BsBookmarkHeart
                      size={35}
                      onClick={() => this.props.addFavorites(libraryItem.book)}
                    />
                  </CardBody>
                </Card>
              ))}
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
  renderEmptyLibrary() {
    return (
      <NavItem>
        <NavLink>Empty library</NavLink>
      </NavItem>
    );
  }
  render() {
    return (
      <div>
        {this.props.library.length > 0
          ? this.renderSummary()
          : this.renderEmptyLibrary()}
      </div>
    );
  }
}
