import React, { useState, useEffect, Component } from "react";
import alertify from "alertifyjs";
import axios from "axios";
import "./Home.css";
import Navi from "../components/Navi";
import Library from "./Library";
import { IoHeartOutline } from "react-icons/io5";
import {
  Col,
  Container,
  Row,
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  CardText,
  Button,
  NavItem,
  NavLink,
} from "reactstrap";

export default class Home extends Component {
  state = { books: [] };
  // state = { library: [] };

  componentDidMount() {
    this.getBooks();
  }

  getBooks = () => {
    let url = "http://localhost:3000/books";

    fetch(url)
      .then((response) => response.json())
      .then((data) => this.setState({ books: data }));
  };

  // addToLibrary = (book) => {
  //   let newLibrary = this.state.library;

  //   newLibrary.push({ book: book });

  //   this.setState({ library: newLibrary });
  //   alertify.success(book.title + " added to library!");
  // };

  // removeFromLibrary = (book) => {
  //   let newLibrary = this.state.library.filter((c) => c.book.id !== book.id);
  //   this.setState({ library: newLibrary });
  //   alertify.error(book.title + " removed from library!");
  // };
  render() {
    // const { books } = this.state;
    return (
      <div>
        <Navi />

        <ItemLibrary data={this.state.books} />
      </div>
    );
  }
}
const ItemLibrary = (props) => (
  <Container>
    <Row>
      <Col xs="3">
        {props.books.map((book, i) => (
          <Card key={book.id}>
            <CardImg alt="cover" src={book.cover} top width="100%" />
            <CardBody>
              <CardTitle tag="h5">{book.title}</CardTitle>
              <CardSubtitle className="mb-2 text-muted" tag="h6">
                {book.author}
              </CardSubtitle>
              <Button outline>Read</Button>{" "}
              <Button color="danger" outline>
                Remove
              </Button>{" "}
              <IoHeartOutline size={35} />
            </CardBody>
          </Card>
        ))}
      </Col>
    </Row>
  </Container>
);
