import { BrowserRouter, Routes, Route } from "react-router-dom";
import alertify from "alertifyjs";
import Login from "./pages/Login";
import Home from "./pages/Home";
import Profile from "./pages/Profile";

export default class App extends Component {
  addToLibrary = (book) => {
    let newLibrary = this.state.library;
    var addedItem = newLibrary.find((c) => c.book.id === book.id);
    if (addedItem) {
      addedItem = 1;
    } else {
      newLibrary.push({ book: book });
    }
    this.setState({ book: newLibrary });
    alertify.success(book.title + " added to library!");
  };

  removeFromLibrary = (book) => {
    let newLibrary = this.state.library.filter((c) => c.book.id !== book.id);
    this.setState({ library: newLibrary });
    alertify.error(book.title + " removed from library!");
  };
  render() {
    let libraryInfo = { title: "My library" };
    return (
      <div className="App">
        <BrowserRouter>
          <div className="content">
            <Routes>
              <Route
                exact
                path="/"
                render={(props) => (
                  <Home
                    {...props}
                    library={this.state.library}
                    removeFromLibrary={this.removeFromLibrary}
                  />
                )}
              />
              <Route exact path="/home" element={<Home />} />
              <Route exact path="/profile" element={<Profile />} />
            </Routes>
          </div>
        </BrowserRouter>
      </div>
    );
  }
}
