import React, { Component, Fragment } from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    <React.Fragment>
      <Card
        key={book.id}
        className="m-3 p-3"
        style={{
          borderRadius: "15px",
          flex: "1 1 11rem",
          display: "flex",
          flexWrap: "wrap",
          flexDirection: "column",
        }}
      >
        <CardImg
          alt="cover"
          src={book.cover}
          top
          width="100%"
          className="p-3"
          style={{ maxHeight: "280px" }}
        />
        <CardBody>
          <CardTitle tag="h5">{book.title}</CardTitle>
          <CardSubtitle className="mb-2 text-muted" tag="h6">
            {book.author}
          </CardSubtitle>
          <a href={book.url}>
            <Button outline style={{ position: "absolute", bottom: "15px" }}>
              Read
            </Button>
          </a>{" "}
          {/* <Button color="danger" outline>
                  Remove
                </Button>{" "} */}
          <IoHeartOutline size={35} style={{ position: "absolute", bottom: "15px", right:"20px"}}/>
        </CardBody>
      </Card>
    </React.Fragment>
  );
};
export default ItemLibrary;
