import { Component } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import alertify from "alertifyjs";
import Login from "./pages/Login";
import Home from "./pages/Home";
import Profile from "./pages/Profile";

export default class App extends Component {
  state = { books: [] };

  componentDidMount() {
    this.getBooks();
  }

  getBooks = () => {
    let url = "http://localhost:3000/books";

    fetch(url)
      .then((response) => response.json())
      .then((data) => this.setState({ books: data }));
  };

  addToLibrary = (book) => {
    let newLibrary = this.state.library;

    newLibrary.push({ book: book });

    this.setState({ library: newLibrary });
    alertify.success(book.title + " added to library!");
  };

  removeFromLibrary = (book) => {
    let newLibrary = this.state.library.filter((c) => c.book.id !== book.id);
    this.setState({ library: newLibrary });
    alertify.error(book.title + " removed from library!");
  };
  render() {
    return (
      <div className="App">
        <BrowserRouter>
          <div className="content">
            <Routes>
              <Route exact path="/" element={<Login />} />
              <Route
                exact
                path="/home"

              />
              <Route exact path="/profile" element={<Profile />} />
            </Routes>
          </div>
        </BrowserRouter>
      </div>
    );
  }
}
