import React, { Component, Fragment } from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  CardText,
  Button,
} from "reactstrap";

export default class ItemLibrary extends Component {
  render() {
    return (
      <React.Fragment>
        {this.props.books.map((item) => (
          <Card item key={item.id}>
            <CardImg alt="cover" src={item.cover} top width="100%" />
            <CardBody>
              <CardTitle tag="h5">{item.title}</CardTitle>
              <CardSubtitle className="mb-2 text-muted" tag="h6">
                {item.author}
              </CardSubtitle>
              <Button outline>Read</Button>{" "}
              <Button color="danger" outline>
                Remove
              </Button>{" "}
              <IoHeartOutline size={35} />
            </CardBody>
          </Card>
        ))}
      </React.Fragment>
    );
  }
}
