import React, { Component, Fragment } from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    <React.Fragment>
      <div style={{ float: "left" }} className="col-sm-4">
        <Card
          key={book.id}
          style={{ borderRadius: "15px", backgroundColor: "#EEEEEE" }}
        >
          <CardImg
            alt="cover"
            src={book.cover}
            top
            width="200px"
            className="p-3"
          />
          <CardBody>
            <CardTitle tag="h5">{book.title}</CardTitle>
            <CardSubtitle className="mb-2 text-muted" tag="h6">
              {book.author}
            </CardSubtitle>
            <a href={book.url}>
              <Button outline>Read</Button>
            </a>{" "}
            {/* <Button color="danger" outline>
                  Remove
                </Button>{" "} */}
            <IoHeartOutline size={30} style={{ float: "right" }} />
          </CardBody>
        </Card>
      </div>
    </React.Fragment>
  );
};
export default ItemLibrary;
