import React, { Component, Fragment } from "react";
import { IoHeartOutline } from "react-icons/io5";
import { Link } from "react-router-dom";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    <React.Fragment>
      <Container className="mt-3">
        <Row>
          <Col xs="3">
            <Card key={book.id}>
              <CardImg alt="cover" src={book.cover} top width="100%" />
              <CardBody>
                <CardTitle tag="h5">{book.title}</CardTitle>
                <CardSubtitle className="mb-2 text-muted" tag="h6">
                  {book.author}
                </CardSubtitle>
                <Link to={book.url}>
                  <Button outline>Read</Button>
                </Link>{" "}
                <Button color="danger" outline>
                  Remove
                </Button>{" "}
                <IoHeartOutline size={30} style={{ float: "right" }} />
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    </React.Fragment>
  );
};
export default ItemLibrary;
