import React, { useState } from "react";
import { Link } from "react-router-dom";
import {
  Navbar,
  NavbarToggler,
  Collapse,
  Nav,
  NavItem,
  NavLink,
} from "reactstrap";
import JSONDATA from "../books.json";
import Avatar from "react-avatar";
import { IoClose, IoSearch } from "react-icons/io5";
import { SidebarData } from "./SidebarData";
import SearchBox from "./SearchBox";
import "./Navi.css";

const Navi = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const showNavbar = () => setIsOpen(!isOpen);

  const [sidebar, setSidebar] = useState(false);
  const showSidebar = () => setSidebar(!sidebar);

  const [filteredData, setFilteredData] = useState("");

  // const handleFilter = (event) => {
  //   const searchWord = event.target.value;
  //   const newFilter = data.books.filter((value) => {
  //     return value.title.toLowerCase().includes(searchWord.toLowerCase());
  //   });
  //   if (searchWord === "") {
  //     setFilteredData([]);
  //   } else {
  //     setFilteredData(newFilter);
  //   }
  // };

  return (
    <div>
      <div>
        <Navbar color="light" expand="md" light>
          <NavLink to="#" className="menu-bars">
            <Avatar
              name="Foo Bar"
              round={true}
              size={40}
              onClick={showSidebar}
              className="avatar"
            />
          </NavLink>
          <NavbarToggler onClick={showNavbar} />
          <Collapse navbar isOpen={isOpen}>
            <Nav className="me-auto" navbar>
              <NavItem>
                <NavLink href="/favorites">My favorites</NavLink>
              </NavItem>
            </Nav>
            <div className="search">
              <div className="d-flex">
                <input
                  className="form-control me-2"
                  type="search"
                  placeholder="Search"
                  aria-label="Search"
                  value={props.value}
                  onChange={(event) =>
                    props.setFilteredData(event.target.value)
                  }
                />
              </div>
            </div>
          </Collapse>
        </Navbar>
        <Nav
          className={sidebar ? "nav-menu active" : "nav-menu"}
          style={{ zIndex: 1 }}
        >
          <ul className="nav-menu-items" onClick={showSidebar}>
            <li className="navbar-toggle">
              <Link to="#" className="menu-bars">
                <IoClose />
              </Link>
            </li>
            {SidebarData.map((item, index) => {
              return (
                <li key={index} className={item.cName}>
                  <Link to={item.path}>
                    {item.icon}
                    <span className="item-title">{item.title}</span>
                  </Link>
                </li>
              );
            })}
          </ul>
        </Nav>
      </div>
    </div>
  );
}

export default Navi;
