import React, { useState } from "react";
import Navi from "../components/Navi";
import ItemLibrary from "../components/ItemLibrary";
import { Container, Row } from "reactstrap";
const Home = ({ books }) => {
  const [filteredData, setFilteredData] = useState("");

  return (
    <div>
      <Navi />
      <div
        className="pt-2"
        style={{ display: "table", marginLeft: "auto", marginRight: "auto" }}
      >
        <input
          className="form-control"
          type="search"
          placeholder="Search"
          aria-label="Search"
          onChange={(event) => setFilteredData(event.target.value)}
          style={{ width: "200px" }}
        />
      </div>
      <Container style={{width:"1100px",maxWidth:"100%",margin:"0 auto"}}>
        <Row style={{display:"flex",alignItems:"center", justifyContent:"space-between"}}>
          {books
            .filter((book) => {
              if (filteredData === "") {
                return book;
              } else if (
                book.title.toLowerCase().includes(filteredData.toLowerCase())
              ) {
                return book;
              }
            })
            .map((book, index) => (
              <ItemLibrary key={index} book={book} />
            ))}
        </Row>
      </Container>
    </div>
  );
};
export default Home;
