import React, { Component, Fragment } from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    <React.Fragment>
      <Card key={book.id} className="m-3 p-3" style={{ borderRadius:"15px", flex: "1 1 11rem", display:"flex",flexWrap:"wrap", alignItems:"center",flexDirection:"column",alignSelf:"normal" }}>
        <CardImg
          alt="cover"
          src={book.cover}
          top
          width="100%"
          className="p-3"
          style={{maxWidth:"250px", maxHeight:"250px"}}
        />
        <CardBody>
          <CardTitle tag="h5">{book.title}</CardTitle>
          <CardSubtitle className="mb-2 text-muted" tag="h6">
            {book.author}
          </CardSubtitle>
          <a href={book.url}>
            <Button outline style={{display:"inline-block",marginTop:"auto"}}>Read</Button>
          </a>{" "}
          {/* <Button color="danger" outline>
                  Remove
                </Button>{" "} */}
          <IoHeartOutline size={35} />
        </CardBody>
      </Card>
    </React.Fragment>
  );
};
export default ItemLibrary;
