import React, { Component } from "react";
import {
  Col,
  Container,
  Row,
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  CardText,
  Button,
  NavItem,
  NavLink,
} from "reactstrap";
import { IoHeartOutline } from "react-icons/io5";

export default class Library extends Component {

  render() {
    return (
      <div>
        <Container>
          <Row>
            <Col xs="3">
              <Card key={book.id}>
                <CardImg alt="cover" top width="100%" />
                <CardBody>
                  <CardTitle tag="h5">{libraryItem.book.title}</CardTitle>
                  <CardSubtitle className="mb-2 text-muted" tag="h6">
                    {}
                  </CardSubtitle>
                  <CardText>{libraryItem.book.tags}</CardText>
                  <Button outline onClick={() => this.props.readBook()}>
                    Read
                  </Button>{" "}
                  <Button
                    color="danger"
                    outline
                    onClick={() => this.props.removeFromLibrary()}
                  >
                    Remove
                  </Button>{" "}
                  <IoHeartOutline
                    size={35}
                    onClick={() => this.props.addFavorites()}
                  />
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
