import React, { Component } from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  CardText,
  Button,
} from "reactstrap";

const ItemLibrary = (props) => {
  const { addItemToFavorites } = useFavorites();
  return (
    <div>
      <Card>
        <CardImg alt="cover" src={props.cover} top width="100%" />
        <CardBody>
          <CardTitle tag="h5">{props.title}</CardTitle>
          <CardSubtitle className="mb-2 text-muted" tag="h6">
            {props.author}
          </CardSubtitle>
          <CardText>{props.tags}</CardText>
          <Button outline>Read</Button>{" "}
          <Button color="danger" outline>
            Remove
          </Button>{" "}
          <IoHeartOutline size={35} />
        </CardBody>
      </Card>
    </div>
  );
};
export default ItemLibrary;
