import React from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    <React.Fragment>
      <Card
        key={book.id}
        className="m-3 p-3"
        style={{
          borderRadius: "15px",
          flex: "1 1 21rem",
          display: "flex",
          flexWrap: "wrap",
          flexDirection: "column",
          alignSelf: "normal",
          maxWidth: "250px",
        }}
      >
        <CardImg
          alt="cover"
          src={book.formats["image/jpeg"]}
          top
          width="100%"
          style={{ maxHeight: "300px" }}
        />
        <CardBody>
         {book.authors.name.map((name) =>
          <CardSubtitle className="mb-2 text-muted" tag="h6">
            {name}
          </CardSubtitle>
          )}
          
          <a href={book.url}>
            <Button outline>Read</Button>
          </a>{" "}
          {/* <Button color="danger" outline>
                  Remove
                </Button>{" "} */}
          <IoHeartOutline
            size={30}
            style={{ position: "absolute", bottom: "35", right: "35" }}
          />
        </CardBody>
      </Card>
    </React.Fragment>
  );
};
export default ItemLibrary;
