import React, { Component, Fragment } from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
  Container,
  Row,
  Col,
  CardGroup
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    <React.Fragment>
      <CardGroup>
        <Card
          key={book.id}
          style={{ borderRadius: "15px", backgroundColor: "#EEEEEE" }}
          className="m-3"
        >
          <CardImg
            alt="cover"
            src={book.cover}
            top
            width="100%"
            className="p-3"
            style={{ height: "30vw" }}
          />
          <CardBody style={{ height: "11vw" }}>
            <CardTitle tag="h5">{book.title}</CardTitle>
            <CardSubtitle className="mb-2 text-muted" tag="h6">
              {book.author}
            </CardSubtitle>
            <a href={book.url}>
              <Button outline style={{ position: "absolute", bottom: "10px" }}>
                Read
              </Button>
            </a>{" "}
            {/* <Button color="danger" outline>
                  Remove
                </Button>{" "} */}
            <IoHeartOutline
              size={35}
              style={{
                position: "absolute",
                bottom: "15px",
                right: "15px",
              }}
            />
          </CardBody>
        </Card>
      </CardGroup>
    </React.Fragment>
  );
};
export default ItemLibrary;
