import React, { Component } from "react";
import {
  Col,
  Container,
  Row,
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  CardText,
  Button,
  NavItem,
  NavLink,
} from "reactstrap";
import { IoHeartOutline } from "react-icons/io5";

export default class Library extends Component {
  
  state = {library:[]}
  getBooks = () => {
    fetch("http://localhost:3000/books")
    .then(response => response.json())
    .then(data => this.setState({books:data}))
  }
  render() {
    return (
      <div>
        <Container>
          <Row>
            <Col xs="3">
              {this.state.books.map(book => (
                <Card>
                  <CardImg
                    alt="cover"
                    src={book.cover}
                    top
                    width="100%"
                  />
                  <CardBody>
                    <CardTitle tag="h5"></CardTitle>
                    <CardSubtitle className="mb-2 text-muted" tag="h6">
                      {}
                    </CardSubtitle>
                    <CardText></CardText>
                    <Button
                      outline
                    >
                      Read
                    </Button>{" "}
                    <Button
                      color="danger"
                      outline
                    >
                      Remove
                    </Button>{" "}
                    <IoHeartOutline
                      size={35}
                    />
                  </CardBody>
                </Card>
              ))}
              
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
