import React, { Component, Fragment } from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    <React.Fragment>
      <article style={{display:block}}>
        <div className="col-md-3" style={{ float: "left" }}>
        <Card
          key={book.id}
          style={{ borderRadius: "15px", backgroundColor: "#EEEEEE" }}
          className="m-3"
        >
          <CardImg
            alt="cover"
            src={book.cover}
            top
            width="100%"
            className="p-3"
          />
          <CardBody>
            <CardTitle tag="h5">{book.title}</CardTitle>
            <CardSubtitle className="mb-2 text-muted" tag="h6">
              {book.author}
            </CardSubtitle>
            <a href={book.url}>
              <Button outline>Read</Button>
            </a>{" "}
            {/* <Button color="danger" outline>
                  Remove
                </Button>{" "} */}
            <IoHeartOutline size={30} style={{ float: "right" }} />
          </CardBody>
        </Card>
      </div>
      </article>
      
    </React.Fragment>
  );
};
export default ItemLibrary;
