import React, { Component, Fragment } from "react";
import { IoHeartOutline } from "react-icons/io5";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";

const ItemLibrary = ({ book }) => {
  return (
    
            <Card key={book.id} style={{borderRadius: "15px"}}>
              <CardImg alt="cover" src={book.cover} top width="100%" className="p-3"/>
              <CardBody>
                <CardTitle tag="h5">{book.title}</CardTitle>
                <CardSubtitle className="mb-2 text-muted" tag="h6">
                  {book.author}
                </CardSubtitle>
                <a href={book.url}>
                  <Button outline>Read</Button>
                </a>{" "}
                {/* <Button color="danger" outline>
                  Remove
                </Button>{" "} */}
                <IoHeartOutline size={30} style={{ float: "right" }} />
              </CardBody>
            </Card>
          
  );
};
export default ItemLibrary;
